import React from "react";
import { useQuery, gql } from "@apollo/client";
const QUERY_ALL_USERS = gql`
  query getAllUsers{
    users {
      id,
      name,
      age,
      username,
      nationality,
      friends {
        name,
        age
      }
    }
  }
`

function DisplayData(){
  const {data, loading} = useQuery(QUERY_ALL_USERS);

  if(data){
    console.log(data);
  }
  return <div></div>
}

export default DisplayData;
