import React from "react";
import { useQuery, gql } from "@apollo/client";
const QUERY_ALL_USERS = gql`
  query getAllUsers{
    users {
      id,
      name,
      age,
      username,
      nationality,
      friends {
        name,
        age
      }
    }
  }
`

function DisplayData(){
  const {data, loading, error} = useQuery(QUERY_ALL_USERS);
  if(loading){
    <h1>Loading ...</h1>
  }
  if(error){
    console.log(error);
  }
  if(data){
    console.log(data);
  }
  return <div></div>
}

export default DisplayData;
